CREATE TABLE IF NOT EXISTS ocha.contacts (
    `id` INT,
    `nombre` VARCHAR(54) CHARACTER SET 'utf8' COLLATE 'utf8_spanish2_ci',
    `telefono` VARCHAR(92) CHARACTER SET 'utf8' COLLATE 'utf8_spanish2_ci',
    `fax` VARCHAR(19) CHARACTER SET 'utf8' COLLATE 'utf8_spanish2_ci',
    `correo` VARCHAR(48) CHARACTER SET 'utf8' COLLATE 'utf8_spanish2_ci',
    `celular` VARCHAR(25) CHARACTER SET 'utf8' COLLATE 'utf8_spanish2_ci',
    `apellido` VARCHAR(20) CHARACTER SET 'utf8' COLLATE 'utf8_spanish2_ci',
    `municipio` INT,
    `municipioResidencia` INT,
    `departamentoResidencia` INT,
    `fechaActualizacion` DATETIME,
    `idtematica` INT,
    `idorganizacion` INT NULL,
    PRIMARY KEY (`id`),
    FOREIGN KEY (`idtematica`) REFERENCES thematics(`id`),
    FOREIGN KEY (`idorganizacion`) REFERENCES organizations(`id`)
);

